sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },
	isDatePast: function(strDate){
		// creo 2 nuove date
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);

		// controlla che le richieste non vengano eseguite per date precedenti o per lo stesso giorno
		if (testdate.before(rightnow)) {
			return true;
		}
		else {
			return false;
		}
	},

		isDateTomorrow: function(strDate){

		// Controlla anche che l'opzione non sia valida per il giorno successivo
		var tomorrow = new GlideDateTime();
		var test = new GlideDateTime(strDate);
		// aggiungo 1 per calcolare domani
		tomorrow.addDaysLocalTime(1);
		// Confronto oggi con domani
		if(test.before(tomorrow) ){
			return true;
		}
		else {
			return false;
		}
	},

    type: 'NeedItUtils'
};
